(283, 315, '_wp_attachment_metadata', 'a:5:{s:5:"width";i:3276;s:6:"height";i:2453;s:4:"file";s:32:"2014/09/QC__0094_2895_120dpi.jpg";s:5:"sizes";a:9:{s:9:"thumbnail";a:4:{s:4:"file";s:32:"QC__0094_2895_120dpi-150x150.jpg";s:5:"width";i:150;s:6:"height";i:150;s:9:"mime-type";s:10:"image/jpeg";}s:6:"medium";a:4:{s:4:"file";s:32:"QC__0094_2895_120dpi-300x225.jpg";s:5:"width";i:300;s:6:"height";i:225;s:9:"mime-type";s:10:"image/jpeg";}s:5:"large";a:4:{s:4:"file";s:33:"QC__0094_2895_120dpi-1024x767.jpg";s:5:"width";i:1024;s:6:"height";i:767;s:9:"mime-type";s:10:"image/jpeg";}s:11:"blog-widget";a:4:{s:4:"file";s:30:"QC__0094_2895_120dpi-50x50.jpg";s:5:"width";i:50;s:6:"height";i:50;s:9:"mime-type";s:10:"image/jpeg";}s:15:"portfolio-thumb";a:4:{s:4:"file";s:32:"QC__0094_2895_120dpi-600x403.jpg";s:5:"width";i:600;s:6:"height";i:403;s:9:"mime-type";s:10:"image/jpeg";}s:16:"portfolio-widget";a:4:{s:4:"file";s:32:"QC__0094_2895_120dpi-100x100.jpg";s:5:"width";i:100;s:6:"height";i:100;s:9:"mime-type";s:10:"image/jpeg";}s:4:"wide";a:4:{s:4:"file";s:33:"QC__0094_2895_120dpi-1000x500.jpg";s:5:"width";i:1000;s:6:"height";i:500;s:9:"mime-type";s:10:"image/jpeg";}s:7:"regular";a:4:{s:4:"file";s:32:"QC__0094_2895_120dpi-500x500.jpg";s:5:"width";i:500;s:6:"height";i:500;s:9:"mime-type";s:10:"image/jpeg";}s:4:"tall";a:4:{s:4:"file";s:33:"QC__0094_2895_120dpi-500x1000.jpg";s:5:"width";i:500;s:6:"height";i:1000;s:9:"mime-type";s:10:"image/jpeg";}}s:10:"image_meta";a:11:{s:8:"aperture";i:0;s:6:"credit";s:0:"";s:6:"camera";s:0:"";s:7:"caption";s:0:"";s:17:"created_timestamp";i:0;s:9:"copyright";s:0:"";s:12:"focal_length";i:0;s:3:"iso";i:0;s:13:"shutter_speed";i:0;s:5:"title";s:0:"";s:11:"orientation";i:0;}}');
